package Operators.in.Java;

public class BitwiseOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	// Bitwise operators &, ^,  |;
		int a=3;
		int b= 4;
		
		int c= a & b;
		System.out.println(c);
		
		int d= a|b;
		System.out.println(d);
		
		int e= a^b;
		System.out.println(e);
		
	}

}
